import { VisibilityFiltersArticle } from '../actions';

export default function articlesFilter(state = [] , action){
  console.log('action filter', action.filter)
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER_ARTICLE':
      return state.map(
        articles =>
          articles.id === action.id ? { ...articles } : null
      )
    default:
      return state
  }
}
