import { connect } from 'react-redux'
import { setVisibilityFilteArticle } from '../actions'
import FilterSelect from '../components/FilterSelect'

const mapStateToProps = (state, ownProps) => ({
//	active : ownProps.filter === state.visibilityFilter
})

const mapDispatchToProps = (dispatch, ownProps) => ({
  onChange: (ev) => {
  	const {selectedOptions} = ownProps;
  	console.log('container props-', ownProps)
  	console.log('container-', selectedOptions)
	return dispatch(setVisibilityFilteArticle(selectedOptions))}
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(FilterSelect)
