import { VisibilityFiltersArticle } from '../actions';

export default function articlesFilter(state = {visibleArticles: []}, action){
  console.log(action.type)
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER_ARTICLE':
      return action.filter
    default:
      return state
  }
}
