import { VisibilityFiltersArticle } from '../actions';

export default function articlesFilter(state = VisibilityFiltersArticle.SHOW_ARTICLE_ALL, action){
  console.log(action.type)
  switch (action.type) {
    case 'SET_VISIBILITY_FILTER_ARTICLE':
      return state.visibleArticles.filter((article) => {return state.visibleArticles.id === action.id})
    default:
      return state
  }
}
