import React, {Component} from 'react';
import { connect } from 'react-redux';
import Select from 'react-select';
import {setVisibilityFilteArticle} from '../actions';
import {articles} from '../fixtures';

const options = articles.map((article)=>{return {value: article.id, label: article.title}});

class FilterSelect extends Component {
  state = {
    selectedOption: null,
  }

  render() {
    const { selectedOption } = this.state;
    return (
      <Select
        value={selectedOption}
        onChange={(val, item) => { this.setState({selectedOption: val}); this.props.onChangeSelect(item)}}
        options={options}
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => ({
//  active : ownProps.filter === state.visibilityFilter
})

const mapDispatchToProps = dispatch => {
 return {
    onChangeSelect: id => {
      dispatch(setVisibilityFilteArticle(id))
    }
  }
}

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(FilterSelect)
