import { connect } from 'react-redux'
import ArticleList from '../components/ArticleList'
import { VisibilityFiltersArticle } from '../actions'
import {articles as defaultArticles} from '../fixtures'


const getVisibleArticle = (articles, filter) => {
  switch (filter) {
    case VisibilityFiltersArticle.SHOW_ARTICLE_ALL:
      return articles
    case VisibilityFiltersArticle.SHOW_ARTICLE_BY_ID:
      return articles.filter(id => articles.id === id)
    case VisibilityFiltersArticle.SHOW_ARTICLE_BY_DATE:
      return articles
    default:
      throw new Error('Unknown filter: ' + filter)
  }
}

const mapStateToProps = state => ({
  articles1: getVisibleArticle(defaultArticles, state.articlesFilter)
})

const mapDispatchToProps = dispatch => ({
//  toggleTodo: id => dispatch(toggleTodo(id))
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(ArticleList)
